func TestHash__Equivalence__Resize(t *testing.T) {
    f, i := getTestImage(testImagePng1Big)
    defer f.Close()

    bh := NewBlockhash(i, 16)
    original := bh.Hexdigest()

    // Test resize up.

    resized1 := resize.Resize(5000, 5000, i, resize.Lanczos3)

    bh = NewBlockhash(resized1, 16)
    hash1 := bh.Hexdigest()

    if hash1 != original {
        t.Fatalf("hash 1 of resized image doesn't match the original")
    }

    // Test resize down 1.

    resized2 := resize.Resize(1000, 1000, i, resize.Lanczos3)

    bh = NewBlockhash(resized2, 16)
    hash2 := bh.Hexdigest()

    if hash2 != original {
        t.Fatalf("hash 2 of resized image doesn't match the original")
    }

    // Test resize down 2.

    resized3 := resize.Resize(500, 500, i, resize.Lanczos3)

    bh = NewBlockhash(resized3, 16)
    hash3 := bh.Hexdigest()

    if hash3 != original {
        t.Fatalf("hash 3 of resized image doesn't match the original")
    }

    // Test resize down 3.

    resized4 := resize.Resize(100, 100, i, resize.Lanczos3)

    bh = NewBlockhash(resized4, 16)
    hash4 := bh.Hexdigest()

    if hash4 != original {
        t.Fatalf("hash 4 of resized image doesn't match the original")
    }

    // Test resize down 4.

    resized5 := resize.Resize(50, 50, i, resize.Lanczos3)

    bh = NewBlockhash(resized5, 16)
    hash5 := bh.Hexdigest()

    if hash5 != original {
        t.Fatalf("hash 5 of resized image doesn't match the original")
    }
}

func TestTotalValue__WithAlpha(t *testing.T) {
    f, bh := getTestBh(testImageJpeg1Big)
    defer f.Close()

    r := image.Rectangle{
            Min: image.Point{ X: 0, Y: 0 },
            Max: image.Point{ X: 1, Y: 1 },
    }

    i := image.NewRGBA(r)

    // Verify that we get a simple sum that is correctly comprised of the
    // R, G, B components, but not the A (ignored unless it's totally opaque).

    i.Set(0, 0, color.RGBA{R: 1, G: 2, B: 4, A: 1})

    p := i.At(0, 0)
    v := bh.totalValue(p)

    if v != 7 {
        t.Fatalf("calculated color value 1 not correct: [%v]", v)
    }

    // Verify that we get the sum associated with total opaqueness if we're
    // actually completely transparent.

    i.Set(0, 0, color.RGBA{R: 0, G: 0, B: 0, A: 0})

    p = i.At(0, 0)
    v = bh.totalValue(p)

    if v != 765 {
        t.Fatalf("calculated color value 2 not correct: [%v]", v)
    }
}
